mapper.select:

where bw.is_delete='0' and bw.is_left='0' and bw.is_loss='0'
     and bw.user_id like concat('%', #{userId}, '%')
     and bw.book_id = #{bookId}
     and bw.book_flag = #{bookFlag}
     and bw.is_renew = #{isRenew}
     and bw.is_left = #{isLeft}
     and bw.is_loss = #{isLoss}
     ${params.dataScope}

select bw.borrow_id, bw.user_id, bw.book_id, bw.book_flag, bw.borrow_time,
       bw.expire_time, bw.return_time, bw.is_renew, bw.is_left, bw.is_loss, bw.is_delete,
       bk.book_name,bk.book_isbn,
       us.user_name,us.nick_name,us.email
from lib_borrow bw
         LEFT JOIN lib_book bk ON bw.book_id=bk.book_id
         LEFT JOIN sys_user us ON bw.user_id=us.user_id
where bw.is_delete='0' and bw.is_left='1'
     and bw.user_id like concat('%', #{userId}, '%')
     and bw.book_id = #{bookId}
     and bw.book_flag = #{bookFlag}
     and bw.is_renew = #{isRenew}
     and bw.is_left = #{isLeft}
     and bw.is_loss = #{isLoss}
     ${params.dataScope}

mapper.update:
update lib_borrow
user_id = #{userId}, book_id = #{bookId}, book_flag = #{bookFlag}, borrow_time = #{borrowTime}, expire_time = #{expireTime}, return_time = #{returnTime}, is_renew = #{isRenew}, is_left = #{isLeft}, is_loss = #{isLoss}, is_delete = #{isDelete},
where borrow_id = #{borrowId}
